    static ArrayList<Kisah> getListData() {
        ArrayList<Kisah> list = new ArrayList<>();
        for (int position = 0; position < nama.length; position++) {
            Kisah asma = new Kisah();
            asma.setNama(nama[position]);
            asma.setDesk(desk[position]);
            asma.setMujizat(mujizat[position]);
            asma.setGambar(gambar[position]);
            asma.setBanner(banner[position]);
            list.add(asma);
        }
        return list;
    }
